  // Initialize the module
  init: async function() {
    try {
      if (this.initialized) {
        return this;
      }

      AppLogger.log("Initializing Journey Tracker module");

      // Set up event listeners
      this.setupEventListeners();

      this.initialized = true;
      AppLogger.log("Journey Tracker module initialized successfully");

      return this;
    } catch (error) {
      AppLogger.error("Failed to initialize Journey Tracker module", error);
      throw error;
    }
  },

  // Set up event listeners
  setupEventListeners: function() {
    try {
      // Listen for customer profile open
      window.NovAssistant.events.on('customerProfileOpened', this.loadCustomerJourney.bind(this));

      return true;
    } catch (error) {
      AppLogger.error("Error setting up Journey Tracker event listeners", error);
      return false;
    }
  },

  // Load customer journey data
  loadCustomerJourney: function(customerIndex) {
    try {
      // Get customer data
      const customers = window.NovAssistant.CustomerManagement.getCustomers();
      const customer = customers[customerIndex];

      if (!customer) {
        AppLogger.error("Customer not found", new Error("Invalid customer index"));
        return false;
      }

      // Update journey visualization
      this.updateCustomerJourney(customer);

      return true;
    } catch (error) {
      AppLogger.error("Error loading customer journey", error);
      return false;
    }
  },

  // Update customer journey visualization
  updateCustomerJourney: function(customer) {
    try {
      // Calculate progress percentage
      const progressPercentage = this.calculateJourneyProgress(customer);

      // Update progress percentage display
      const journeyPercentageElement = document.querySelector(
        ".journey-percentage"
      );

      if (journeyPercentageElement) {
        journeyPercentageElement.textContent = `${progressPercentage}% Complete`;
      }

      // Update progress bar
      const progressFillElement = document.getElementById("journeyProgressFill");
      if (progressFillElement) {
        progressFillElement.style.width = `${progressPercentage}%`;
      }

      // Initialize journey data if it doesn't exist
      if (!customer.journeyData) {
        customer.journeyData = {};
      }

      // Update phase statuses
      const phases = document.querySelectorAll(".journey-phase");
      phases.forEach((phase, index) => {
        try {
          // Reset all phase statuses
          phase.classList.remove("active", "completed");

          // Get phase ID
          const phaseId = this.journeyStructure.phases[index].id;

          // Check if phase has any completed milestones
          let phaseHasCompletedMilestones = false;
          let phaseHasInProgressMilestones = false;

          this.journeyStructure.phases[index].milestones.forEach((milestone) => {
            let milestoneCompleted = true;
            let milestoneTasks = 0;
            let milestoneCompletedTasks = 0;

            milestone.tasks.forEach((task) => {
              milestoneTasks++;

              if (
                customer.journeyData[phaseId] &&
                customer.journeyData[phaseId][milestone.id] &&
                customer.journeyData[phaseId][milestone.id][task.id]
              ) {
                milestoneCompletedTasks++;
              } else {
                milestoneCompleted = false;
              }
            });

            if (milestoneCompleted && milestoneTasks > 0) {
              phaseHasCompletedMilestones = true;
            }

            if (
              milestoneCompletedTasks > 0 &&
              milestoneCompletedTasks < milestoneTasks
            ) {
              phaseHasInProgressMilestones = true;
            }
          });

          // Update phase status
          const phaseStatusElement = phase.querySelector(".phase-status");
          if (phaseStatusElement) {
            if (phaseHasCompletedMilestones) {
              phase.classList.add("completed");
              phaseStatusElement.textContent = "Completed";
              phaseStatusElement.className = "phase-status completed";
            } else if (phaseHasInProgressMilestones) {
              phase.classList.add("active");
              phaseStatusElement.textContent = "In Progress";
              phaseStatusElement.className = "phase-status in-progress";
            } else {
              phaseStatusElement.textContent = "Not Started";
              phaseStatusElement.className = "phase-status not-started";
            }
          }
        } catch (e) {
          AppLogger.error(`Error updating phase at index ${index}`, e, {
            phaseIndex: index,
            phaseId: this.journeyStructure.phases[index]?.id || 'unknown'
          });
        }
      });

      // Update all checkboxes to reflect saved state
      document.querySelectorAll(".task-check").forEach((checkbox) => {
        try {
          const taskId = checkbox.id;
          const milestone = checkbox.closest(".milestone");
          const phase = checkbox.closest(".journey-phase");

          if (!milestone || !phase) return;

          // Extract IDs
          const milestoneId = milestone.getAttribute("data-milestone");
          const phaseIndex = Array.from(phases).indexOf(phase);

          if (
            phaseIndex < 0 ||
            phaseIndex >= this.journeyStructure.phases.length
          )
            return;

          const phaseId = this.journeyStructure.phases[phaseIndex].id;

          // Check if task is completed
          checkbox.checked = !!(
            customer.journeyData[phaseId] &&
            customer.journeyData[phaseId][milestoneId] &&
            customer.journeyData[phaseId][milestoneId][taskId]
          );
        } catch (e) {
          AppLogger.error(
            `Error updating task checkbox: ${checkbox.id}`,
            e
          );
        }
      });

      // Update milestone checkboxes
      this.updateMilestoneCheckboxes();

      return true;
    } catch (error) {
      AppLogger.error("Error updating customer journey", error, {
        customerId: customer ? customer.id : 'unknown'
      });
      return false;
    }
  },

  // Calculate journey progress percentage
  calculateJourneyProgress: function(customer) {
    try {
      // If customer doesn't have journey data yet, return 0
      if (!customer.journeyData) return 0;

      let completedTasks = 0;
      let totalTasks = 0;

      // Count completed tasks across all phases and milestones
      this.journeyStructure.phases.forEach((phase) => {
        phase.milestones.forEach((milestone) => {
          milestone.tasks.forEach((task) => {
            totalTasks++;

            // Check if this task is completed in the customer's journey data
            if (
              customer.journeyData &&
              customer.journeyData[phase.id] &&
              customer.journeyData[phase.id][milestone.id] &&
              customer.journeyData[phase.id][milestone.id][task.id]
            ) {
              completedTasks++;
            }
          });
        });
      });

      // Calculate percentage and round to nearest integer
      return totalTasks > 0
        ? Math.round((completedTasks / totalTasks) * 100)
        : 0;
    } catch (error) {
      AppLogger.error("Error calculating journey progress", error, {
        customerData: customer ? customer.id : 'unknown'
      });
      return 0;
    }
  },

  // Update milestone checkboxes based on task completion
  updateMilestoneCheckboxes: function() {
    try {
      document.querySelectorAll(".milestone").forEach((milestone) => {
        const checkboxes = milestone.querySelectorAll(".task-check");
        const milestoneCheckbox = milestone.querySelector(".milestone-check");

        if (!milestoneCheckbox) return;

        // Count completed tasks
        let completed = 0;
        checkboxes.forEach((checkbox) => {
          if (checkbox.checked) completed++;
        });

        // Update milestone checkbox
        if (checkboxes.length > 0 && completed === checkboxes.length) {
          milestoneCheckbox.checked = true;
          milestone.classList.add("completed");
        } else {
          milestoneCheckbox.checked = false;
          milestone.classList.remove("completed");
        }
      });

      return true;
    } catch (error) {
      AppLogger.error("Error updating milestone checkboxes", error);
      return false;
    }
  },

  // Handle task checkbox changes
  handleTaskCheckChange: function(checkbox) {
    try {
      // Get current customer
      const customerIndex = window.NovAssistant.state.currentCustomerIndex;
      if (customerIndex === null) return false;

      const customers = window.NovAssistant.CustomerManagement.getCustomers();
      const customer = customers[customerIndex];
      if (!customer) return false;

      // Get task, milestone, and phase IDs
      const taskId = checkbox.id;
      const milestone = checkbox.closest(".milestone");
      const phase = checkbox.closest(".journey-phase");

      if (!milestone || !phase) return false;

      const milestoneId = milestone.getAttribute("data-milestone");
      const phaseIndex = Array.from(
        document.querySelectorAll(".journey-phase")
      ).indexOf(phase);

      if (phaseIndex < 0 || phaseIndex >= this.journeyStructure.phases.length)
        return false;

      const phaseId = this.journeyStructure.phases[phaseIndex].id;

      // Initialize phase and milestone objects if they don't exist
      if (!customer.journeyData[phaseId]) {
        customer.journeyData[phaseId] = {};
      }

      if (!customer.journeyData[phaseId][milestoneId]) {
        customer.journeyData[phaseId][milestoneId] = {};
      }

      // Update task status
      if (checkbox.checked) {
        customer.journeyData[phaseId][milestoneId][taskId] = true;
      } else {
        delete customer.journeyData[phaseId][milestoneId][taskId];

        // Clean up empty objects
        if (
          Object.keys(customer.journeyData[phaseId][milestoneId]).length === 0
        ) {
          delete customer.journeyData[phaseId][milestoneId];
        }

        if (Object.keys(customer.journeyData[phaseId]).length === 0) {
          delete customer.journeyData[phaseId];
        }
      }

      // Update customer's last modified timestamp
      customer.lastUpdated = new Date().toISOString();

      // Save changes
      window.NovAssistant.CustomerManagement.saveCustomers();

      // Update milestone checkbox state
      this.updateMilestoneCheckboxes();

      // Update progress bar and status
      this.updateCustomerJourney(customer);

      return true;
    } catch (error) {
      AppLogger.error("Error handling task check change", error, {
        checkboxId: checkbox?.id || 'unknown',
        customerIndex: window.NovAssistant.state.currentCustomerIndex || 'unknown'
      });
      return false;
    }
  },

  // Initialize journey flow visualization
  initJourneyFlow: function() {
    try {
      const journeyContainer = document.querySelector('.journey-flow-chart');
      if (!journeyContainer) return false;

      // Clear existing content
      journeyContainer.innerHTML = '';

      // Build journey flow HTML
      this.journeyStructure.phases.forEach((phase) => {
        // Create phase container
        const phaseDiv = document.createElement('div');
        phaseDiv.className = 'journey-phase';

        // Phase header
        phaseDiv.innerHTML = `
          <div class="phase-header">
            <div class="phase-icon">
              <i class="${phase.icon}"></i>
            </div>
            <h3 class="phase-title">${phase.name}</h3>
            <span class="phase-status not-started">Not Started</span>
          </div>
        `;

        // Create milestones
        const milestoneGroup = document.createElement('div');
        milestoneGroup.className = 'milestone-group';

        phase.milestones.forEach((milestone) => {
          const milestoneDiv = document.createElement('div');
          milestoneDiv.className = 'milestone';
          milestoneDiv.setAttribute('data-milestone', milestone.id);

          // Milestone header
          milestoneDiv.innerHTML = `
            <div class="milestone-header" onclick="window.NovAssistant.JourneyTracker.toggleMilestoneDetails(this)">
              <input type="checkbox" class="milestone-check" disabled>
              <h4 class="milestone-title">${milestone.name}</h4>
              <span class="milestone-expand">
                <i class="fas fa-chevron-down"></i>
              </span>
            </div>
            <div class="milestone-details">
              <p>${milestone.description}</p>
              <ul class="task-list">
          `;

          // Add tasks
          milestone.tasks.forEach((task) => {
            milestoneDiv.querySelector('.task-list').innerHTML += `
              <li class="task">
                <input type="checkbox" class="task-check" id="${task.id}" onchange="window.NovAssistant.JourneyTracker.handleTaskCheckChange(this)">
                <label for="${task.id}">${task.name}</label>
              </li>
            `;
          });

          // Close milestone details
          milestoneDiv.querySelector('.milestone-details').innerHTML += `
              </ul>
              <div class="milestone-actions">
                <button class="action-btn" onclick="window.NovAssistant.JourneyTracker.addTaskNote('${phase.id}', '${milestone.id}')">
                  <i class="fas fa-sticky-note"></i> Add Note
                </button>
                <button class="action-btn" onclick="window.NovAssistant.JourneyTracker.markAllTasksComplete('${phase.id}', '${milestone.id}')">
                  <i class="fas fa-check-double"></i> Mark All Complete
                </button>
              </div>
            </div>
          `;

          milestoneGroup.appendChild(milestoneDiv);
        });

        phaseDiv.appendChild(milestoneGroup);
        journeyContainer.appendChild(phaseDiv);
      });

      return true;
    } catch (error) {
      AppLogger.error("Error initializing journey flow", error);
      return false;
    }
  },

