// Customer tracking system
class Customer {
  constructor(name, contactDetails) {
    this.name = name;
    this.contactDetails = contactDetails;
    this.currentStage = "INITIAL_CONTACT";
    this.completedStages = {};
    this.stageData = {};
    this.missingInformation = {};
    this.nextActions = [];
    this.followUpDate = null;
    this.specialNotes = "";
    this.lastUpdated = new Date();
  }

  // Update customer information
  updateStageData(stage, data) {
    this.stageData[stage] = {...(this.stageData[stage] || {}), ...data};
    this.lastUpdated = new Date();
    this.checkStageCompleteness(stage);
  }

  // Check if all required fields for a stage are complete
  checkStageCompleteness(stage) {
    const stageConfig = processStages[stage];
    if (!stageConfig) return false;

    const requiredFields = stageConfig.requiredFields || [];
    const missingFields = requiredFields.filter(field => {
      return !this.stageData[stage] || !this.stageData[stage][field];
    });

    this.missingInformation[stage] = missingFields;

    const isComplete = missingFields.length === 0;
    this.completedStages[stage] = isComplete;

    return isComplete;
  }

  // Get next required actions
  generateNextActions() {
    const stage = this.currentStage;
    const stageConfig = processStages[stage];
    if (!stageConfig) return [];

    let actions = [];

    // Add actions for missing required fields
    const missingFields = this.missingInformation[stage] || [];
    missingFields.forEach(field => {
      actions.push(`Collect ${field} information`);
    });

    // Add checklist items that haven't been completed
    const checklistItems = stageConfig.checklistItems || [];
    checklistItems.forEach(item => {
      if (!this.stageData[stage] || !this.stageData[stage][`completed_${item}`]) {
        actions.push(item);
      }
    });

    // Special case for vehicle procurement with subtypes
    if (stage === "VEHICLE_PROCUREMENT" && this.stageData[stage] && this.stageData[stage].procurementType) {
      const subtype = this.stageData[stage].procurementType;
      const subtypeConfig = stageConfig.subtypes[subtype];
      if (subtypeConfig) {
        const subtypeChecklist = subtypeConfig.checklistItems || [];
        subtypeChecklist.forEach(item => {
          if (!this.stageData[stage] || !this.stageData[stage][`completed_${item}`]) {
            actions.push(item);
          }
        });
      }
    }

    this.nextActions = actions;
    return actions;
  }

  // Advance to next stage
  advanceStage() {
    const stages = Object.keys(processStages);
    const currentIndex = stages.indexOf(this.currentStage);
    if (currentIndex < stages.length - 1) {
      this.currentStage = stages[currentIndex + 1];
      this.generateNextActions();
      return true;
    }
    return false;
  }

  // Generate a summary for the customer
  generateSummary() {
    return {
      customerName: this.name,
      contactDetails: this.contactDetails,
      currentStage: processStages[this.currentStage].name,
      completionStatus: Object.keys(processStages).map(stage => ({
        stage: processStages[stage].name,
        complete: this.completedStages[stage] || false
      })),
      missingInformation: Object.keys(this.missingInformation)
        .filter(stage => this.missingInformation[stage].length > 0)
        .map(stage => ({
          stage: processStages[stage].name,
          missingFields: this.missingInformation[stage]
        })),
      nextActions: this.nextActions,
      followUpDate: this.followUpDate,
      specialNotes: this.specialNotes,
      lastUpdated: this.lastUpdated
    };
  }
}

// Task management system
class TaskManager {
  constructor() {
    this.customers = {};
    this.dailyTasks = [];
    this.upcomingDeadlines = [];
  }

  // Add or update a customer
  updateCustomer(customerId, customerData) {
    if (!this.customers[customerId]) {
      this.customers[customerId] = new Customer(customerData.name, customerData.contactDetails);
    }

    const customer = this.customers[customerId];

    if (customerData.stageData) {
      Object.keys(customerData.stageData).forEach(stage => {
        customer.updateStageData(stage, customerData.stageData[stage]);
      });
    }

    if (customerData.currentStage) {
      customer.currentStage = customerData.currentStage;
    }

    if (customerData.followUpDate) {
      customer.followUpDate = new Date(customerData.followUpDate);
    }

    if (customerData.specialNotes) {
      customer.specialNotes = customerData.specialNotes;
    }

    customer.generateNextActions();
    this.updateDailyTasks();

    return customer;
  }

  // Generate daily task list
  updateDailyTasks() {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    this.dailyTasks = [];
    this.upcomingDeadlines = [];

    // Process each customer
    Object.keys(this.customers).forEach(customerId => {
      const customer = this.customers[customerId];

      // Follow-up tasks due today
      if (customer.followUpDate) {
        const followUpDate = new Date(customer.followUpDate);
        followUpDate.setHours(0, 0, 0, 0);

        if (followUpDate.getTime() === today.getTime()) {
          this.dailyTasks.push({
            type: "FOLLOW_UP",
            customerId,
            customerName: customer.name,
            task: `Follow up with ${customer.name} regarding ${processStages[customer.currentStage].name}`,
            priority: "HIGH"
          });
        } else if (followUpDate > today) {
          // Upcoming follow-ups
          const daysUntil = Math.ceil((followUpDate - today) / (1000 * 60 * 60 * 24));
          if (daysUntil <= 7) {
            this.upcomingDeadlines.push({
              type: "FOLLOW_UP",
              customerId,
              customerName: customer.name,
              task: `Follow up with ${customer.name}`,
              dueDate: followUpDate,
              daysUntil
            });
          }
        }
      }

      // Next actions for each customer
      customer.nextActions.slice(0, 3).forEach(action => {
        this.dailyTasks.push({
          type: "ACTION",
          customerId,
          customerName: customer.name,
          task: action,
          stage: customer.currentStage,
          priority: "MEDIUM"
        });
      });

      // Check for PHEV/EV special deadlines
      if (customer.stageData.VEHICLE_PROCUREMENT && 
          customer.stageData.VEHICLE_PROCUREMENT.isPHEV === true) {
        this.dailyTasks.push({
          type: "DEADLINE",
          customerId,
          customerName: customer.name,
          task: "Ensure PHEV delivery by March 31, 2025 for FBT exemption",
          priority: "CRITICAL"
        });

        // Calculate days until March 31, 2025
        const deadline = new Date(2025, 2, 31); // March 31, 2025
        const daysUntil = Math.ceil((deadline - today) / (1000 * 60 * 60 * 24));

        this.upcomingDeadlines.push({
          type: "PHEV_DEADLINE",
          customerId,
          customerName: customer.name,
          task: "PHEV delivery deadline",
          dueDate: deadline,
          daysUntil
        });
      }

      // Check for payout deadlines for refinances
      if (customer.stageData.APPLICATION_PROCESS && 
          customer.stageData.APPLICATION_PROCESS.isRefinance === true &&
          customer.stageData.APPLICATION_PROCESS.payoutDeadline) {

        const payoutDeadline = new Date(customer.stageData.APPLICATION_PROCESS.payoutDeadline);
        const daysUntil = Math.ceil((payoutDeadline - today) / (1000 * 60 * 60 * 24));

        if (daysUntil <= 14) {
          this.dailyTasks.push({
            type: "DEADLINE",
            customerId,
            customerName: customer.name,
            task: `Refinance payout deadline approaching (${daysUntil} days)`,
            priority: daysUntil <= 5 ? "CRITICAL" : "HIGH"
          });

          this.upcomingDeadlines.push({
            type: "PAYOUT_DEADLINE",
            customerId,
            customerName: customer.name,
            task: "Refinance payout deadline",
            dueDate: payoutDeadline,
            daysUntil
          });
        }
      }
    });

    // Sort tasks by priority
    this.dailyTasks.sort((a, b) => {
      const priorityOrder = { "CRITICAL": 0, "HIGH": 1, "MEDIUM": 2, "LOW": 3 };
      return priorityOrder[a.priority] - priorityOrder[b.priority];
    });

    // Sort deadlines by days until
    this.upcomingDeadlines.sort((a, b) => a.daysUntil - b.daysUntil);
  }

  // Generate daily briefing for the consultant
  generateDailyBriefing() {
    this.updateDailyTasks();

    // Count customers by stage
    const stageCount = {};
    Object.keys(processStages).forEach(stage => {
      stageCount[stage] = 0;
    });

    Object.values(this.customers).forEach(customer => {
      stageCount[customer.currentStage] = (stageCount[customer.currentStage] || 0) + 1;
    });

    return {
      date: new Date().toLocaleDateString(),
      totalCustomers: Object.keys(this.customers).length,
      stageBreakdown: Object.keys(stageCount).map(stage => ({
        stage: processStages[stage].name,
        count: stageCount[stage]
      })),
      criticalTasks: this.dailyTasks.filter(task => task.priority === "CRITICAL"),
      highPriorityTasks: this.dailyTasks.filter(task => task.priority === "HIGH"),
      followUps: this.dailyTasks.filter(task => task.type === "FOLLOW_UP"),
      upcomingDeadlines: this.upcomingDeadlines.filter(deadline => deadline.daysUntil <= 7)
    };
  }

  // Get customer details with current status and next actions
  getCustomerDetails(customerId) {
    const customer = this.customers[customerId];
    if (!customer) return null;

    return customer.generateSummary();
  }

  // Mark a task as complete
  completeTask(customerId, taskDescription) {
    const customer = this.customers[customerId];
    if (!customer) return false;

    const stage = customer.currentStage;
    customer.stageData[stage] = customer.stageData[stage] || {};
    customer.stageData[stage][`completed_${taskDescription}`] = true;

    customer.generateNextActions();
    this.updateDailyTasks();

    return true;
  }

  // Export customer data for CRM integration
  exportCustomerDataForCRM(customerId) {
    const customer = this.customers[customerId];
    if (!customer) return null;

    // Format data for CRM import
    return {
      personalInfo: {
        fullName: customer.name,
        ...customer.contactDetails
      },
      vehicleInfo: customer.stageData.QUOTING_PROCESS || {},
      financeInfo: customer.stageData.APPLICATION_PROCESS || {},
      procurementInfo: customer.stageData.VEHICLE_PROCUREMENT || {},
      currentStage: customer.currentStage,
      nextActions: customer.nextActions,
      notes: customer.specialNotes
    };
  }
}

