import React, { useState } from 'react';
import './Calculator.css';

const Calculator = () => {
  const [formValues, setFormValues] = useState({
    vehiclePrice: 40000,
    leaseTerm: 60, // 5 years in months
    annualSalary: 80000,
    annualKm: 15000
  });

  const [calculationResults, setCalculationResults] = useState({
    monthlyPayment: 754.25,
    carLoanPayment: 892.50,
    cashPurchasePayment: 910.75,
    monthlySavings: 138.25,
    termSavings: 8295
  });

  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormValues({
      ...formValues,
      [id]: value
    });
  };

  const calculateQuote = () => {
    // In a real app, this would do actual calculations
    // For this example, we'll just update with some fixed values

    // Small random adjustment to make it look like it's calculating
    const adjustment = Math.random() * 20 - 10; // Random number between -10 and 10

    setCalculationResults({
      monthlyPayment: (754.25 + adjustment).toFixed(2),
      carLoanPayment: (892.50 + adjustment).toFixed(2),
      cashPurchasePayment: (910.75 + adjustment).toFixed(2),
      monthlySavings: (138.25 + adjustment / 2).toFixed(2),
      termSavings: (8295 + adjustment * 60 / 2).toFixed(0)
    });
  };

  return (
    <div className="card">
      <h2>Novated Lease Calculator</h2>

      <div className="calculator-form">
        <div className="form-group">
          <label htmlFor="vehiclePrice">Vehicle Price ($)</label>
          <input 
            type="number" 
            id="vehiclePrice" 
            value={formValues.vehiclePrice} 
            onChange={handleInputChange}
            min="0" 
            step="1000" 
          />
        </div>

        <div className="form-group">
          <label htmlFor="leaseTerm">Lease Term</label>
          <select 
            id="leaseTerm" 
            value={formValues.leaseTerm}
            onChange={handleInputChange}
          >
            <option value="12">1 Year</option>
            <option value="24">2 Years</option>
            <option value="36">3 Years</option>
            <option value="48">4 Years</option>
            <option value="60">5 Years</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="annualSalary">Annual Salary ($)</label>
          <input 
            type="number" 
            id="annualSalary" 
            value={formValues.annualSalary} 
            onChange={handleInputChange}
            min="0" 
            step="5000" 
          />
        </div>

        <div className="form-group">
          <label htmlFor="annualKm">Annual Kilometers</label>
          <input 
            type="number" 
            id="annualKm" 
            value={formValues.annualKm} 
            onChange={handleInputChange}
            min="0" 
            step="1000" 
          />
        </div>
      </div>

      <button onClick={calculateQuote} className="primary-btn">Calculate Quote</button>

      <div className="calculator-result">
        <h3>Estimated Monthly Payment</h3>
        <div className="amount">${calculationResults.monthlyPayment}</div>
        <p>This includes all running costs and tax benefits</p>
      </div>

      <div className="comparison-results">
        <h3>Comparison with Other Options</h3>
        <p><strong>Car Loan:</strong> ${calculationResults.carLoanPayment}/month</p>
        <p><strong>Cash Purchase:</strong> ${calculationResults.cashPurchasePayment}/month</p>
        <p><strong>Savings vs Car Loan:</strong> ${calculationResults.monthlySavings}/month (${calculationResults.termSavings} over term)</p>
      </div>
    </div>
  );
};

export default Calculator;

