const NovatedLeaseCalculator = () => {
  // Form state
  const [formData, setFormData] = useState({
    vehiclePrice: 40000,
    term: 60, // months
    interestRate: 7.9,
    annualKilometers: 15000,
    annualSalary: 80000,
    fbtMethod: 'ecm', // ECM (Employee Contribution Method) or 'exempt' for ZLEV exempt
    residualValuePercentage: 28.13, // Default for 5 year lease
    vehicleType: 'standard', // standard or 'zlev' for Zero/Low Emission Vehicles
  });

  // Results state
  const [results, setResults] = useState({
    financeAmount: 0,
    residualValue: 0,
    monthlyLease: 0,
    runningCosts: 0,
    totalMonthlyPackage: 0,
    preTaxContribution: 0,
    postTaxContribution: 0,
    taxSavings: 0,
    impactOnTakeHomePay: 0,
    totalSavings: 0,
  });

  // Tax rates for 2024-2025
  const taxRates = [
    { min: 0, max: 18200, rate: 0, base: 0 },
    { min: 18201, max: 45000, rate: 0.19, base: 0 },
    { min: 45001, max: 120000, rate: 0.325, base: 5092 },
    { min: 120001, max: 180000, rate: 0.37, base: 29467 },
    { min: 180001, max: Infinity, rate: 0.45, base: 51667 },
  ];

  // Residual value percentages as per ATO guidelines
  const residualPercentages = {
    12: 65.63, // 1 year
    24: 56.25, // 2 years
    36: 46.88, // 3 years
    48: 37.5,  // 4 years
    60: 28.13, // 5 years
  };

  // Update residual value when term changes
  useEffect(() => {
    if (residualPercentages[formData.term]) {
      setFormData(prev => ({
        ...prev,
        residualValuePercentage: residualPercentages[formData.term]
      }));
    }
  }, [formData.term]);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : 
              type === 'number' ? parseFloat(value) : value
    }));
  };

  // Calculate PMT function (similar to Excel's PMT)
  const calculatePMT = (rate, nper, pv, fv = 0, type = 0) => {
    if (rate === 0) return -(pv + fv) / nper;

    const pvif = Math.pow(1 + rate, nper);
    let pmt = (rate / (pvif - 1)) * -(pv * pvif + fv);

    if (type === 1) {
      pmt = pmt / (1 + rate);
    }

    return pmt;
  };

  // Get tax rate based on income
  const getTaxRate = (annualSalary) => {
    for (const bracket of taxRates) {
      if (annualSalary >= bracket.min && annualSalary <= bracket.max) {
        return bracket.rate;
      }
    }
    return 0;
  };

  // Calculate running costs based on annual kilometers
  const calculateRunningCosts = (annualKm) => {
    // Approximate monthly running costs based on annual kilometers
    const fuelCost = annualKm * 0.12 / 12; // Approx. $0.12 per km for fuel
    const registrationInsurance = 150; // Monthly average
    const maintenanceAndTires = annualKm * 0.05 / 12; // Approx. $0.05 per km

    return fuelCost + registrationInsurance + maintenanceAndTires;
  };

  // Perform calculation
  const performCalculation = () => {
    try {
      // Remove GST from vehicle price
      const vehiclePriceExGST = formData.vehiclePrice / 1.1;

      // Calculate residual value
      const residualValue = vehiclePriceExGST * (formData.residualValuePercentage / 100);

      // Calculate monthly lease payment
      const monthlyInterestRate = formData.interestRate / 100 / 12;
      const monthlyLease = calculatePMT(
        monthlyInterestRate,
        formData.term,
        -vehiclePriceExGST,
        residualValue
      );

      // Calculate running costs
      const runningCosts = calculateRunningCosts(formData.annualKilometers);

      // Calculate total monthly package
      const totalMonthlyPackage = monthlyLease + runningCosts;

      // Calculate pre-tax and post-tax contributions based on FBT method
      let preTaxContribution, postTaxContribution;

      if (formData.fbtMethod === 'exempt' || formData.vehicleType === 'zlev') {
        // ZLEV exempt or other exempt employer
        preTaxContribution = totalMonthlyPackage;
        postTaxContribution = 0;
      } else {
        // ECM method - running costs from pre-tax, finance from post-tax
        preTaxContribution = runningCosts;
        postTaxContribution = monthlyLease;
      }

      // Calculate tax savings
      const taxRate = getTaxRate(formData.annualSalary);
      const taxSavings = preTaxContribution * taxRate;

      // Calculate impact on take-home pay
      const impactOnTakeHomePay = totalMonthlyPackage - taxSavings;

      // Calculate total savings over lease term
      const totalSavings = taxSavings * formData.term;

      // Update results state
      setResults({
        financeAmount: vehiclePriceExGST - residualValue,
        residualValue,
        monthlyLease,
        runningCosts,
        totalMonthlyPackage,
        preTaxContribution,
        postTaxContribution,
        taxSavings,
        impactOnTakeHomePay,
        totalSavings,
      });
    } catch (error) {
      console.error("Calculation error:", error);
    }
  };

  // Trigger calculation when form data changes
  useEffect(() => {
    performCalculation();
  }, [formData]);

  return (
    <div className="novated-lease-calculator">
      <h2>Novated Lease Calculator</h2>

      <div className="calculator-container">
        <div className="calculator-form-section">
          <h3>Vehicle & Lease Details</h3>

          <div className="form-group">
            <label htmlFor="vehiclePrice">Vehicle Price ($)</label>
            <input
              type="number"
              id="vehiclePrice"
              name="vehiclePrice"
              value={formData.vehiclePrice}
              onChange={handleInputChange}
              min="0"
              step="1000"
            />
          </div>

          <div className="form-group">
            <label htmlFor="term">Lease Term</label>
            <select 
              id="term" 
              name="term" 
              value={formData.term}
              onChange={handleInputChange}
            >
              <option value="12">1 Year</option>
              <option value="24">2 Years</option>
              <option value="36">3 Years</option>
              <option value="48">4 Years</option>
              <option value="60">5 Years</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="interestRate">Interest Rate (%)</label>
            <input
              type="number"
              id="interestRate"
              name="interestRate"
              value={formData.interestRate}
              onChange={handleInputChange}
              min="0"
              max="20"
              step="0.1"
            />
          </div>

          <div className="form-group">
            <label htmlFor="annualKilometers">Annual Kilometers</label>
            <input
              type="number"
              id="annualKilometers"
              name="annualKilometers"
              value={formData.annualKilometers}
              onChange={handleInputChange}
              min="0"
              step="1000"
            />
          </div>

          <h3>Salary & Tax Details</h3>

          <div className="form-group">
            <label htmlFor="annualSalary">Annual Salary ($)</label>
            <input
              type="number"
              id="annualSalary"
              name="annualSalary"
              value={formData.annualSalary}
              onChange={handleInputChange}
              min="0"
              step="1000"
            />
          </div>

          <div className="form-group">
            <label htmlFor="vehicleType">Vehicle Type</label>
            <select 
              id="vehicleType" 
              name="vehicleType" 
              value={formData.vehicleType}
              onChange={handleInputChange}
            >
              <option value="standard">Standard Vehicle</option>
              <option value="zlev">Electric/ZLEV (FBT Exempt)</option>
            </select>
          </div>

          {formData.vehicleType === 'standard' && (
            <div className="form-group">
              <label htmlFor="fbtMethod">FBT Method</label>
              <select 
                id="fbtMethod" 
                name="fbtMethod" 
                value={formData.fbtMethod}
                onChange={handleInputChange}
              >
                <option value="ecm">ECM (Employee Contribution)</option>
                <option value="exempt">FBT Exempt Employer</option>
              </select>
            </div>
          )}
        </div>

        <div className="calculator-results-section">
          <h3>Lease Calculation Results</h3>

          <div className="results-card">
            <div className="result-main">
              <div className="result-value">${results.impactOnTakeHomePay.toFixed(2)}</div>
              <div className="result-label">Monthly Impact on Take-Home Pay</div>
            </div>

            <div className="result-grid">
              <div className="result-item">
                <div className="result-label">Finance Amount</div>
                <div className="result-value">${results.financeAmount.toFixed(2)}</div>
              </div>

              <div className="result-item">
                <div className="result-label">Residual Value</div>
                <div className="result-value">${results.residualValue.toFixed(2)}</div>
              </div>

              <div className="result-item">
                <div className="result-label">Monthly Lease Payment</div>
                <div className="result-value">${results.monthlyLease.toFixed(2)}</div>
              </div>

              <div className="result-item">
                <div className="result-label">Monthly Running Costs</div>
                <div className="result-value">${results.runningCosts.toFixed(2)}</div>
              </div>

              <div className="result-item">
                <div className="result-label">Total Monthly Package</div>
                <div className="result-value">${results.totalMonthlyPackage.toFixed(2)}</div>
              </div>

              <div className="result-item">
                <div className="result-label">Pre-Tax Contribution</div>
                <div className="result-value">${results.preTaxContribution.toFixed(2)}</div>
              </div>

              <div className="result-item">
                <div className="result-label">Post-Tax Contribution</div>
                <div className="result-value">${results.postTaxContribution.toFixed(2)}</div>
              </div>

              <div className="result-item highlight">
                <div className="result-label">Monthly Tax Savings</div>
                <div className="result-value">${results.taxSavings.toFixed(2)}</div>
              </div>

              <div className="result-item highlight">
                <div className="result-label">Total Savings Over Lease Term</div>
                <div className="result-value">${results.totalSavings.toFixed(2)}</div>
              </div>
            </div>
          </div>

          <div className="calculator-explanation">
            <h4>How Novated Leasing Works</h4>
            <p>
              A novated lease is a three-way agreement between you, your employer, and a finance company. 
              Your employer takes lease payments from your salary (partly pre-tax), reducing your taxable income.
            </p>
            <p>
              <strong>Key Benefits:</strong>
            </p>
            <ul>
              <li>Pay for your vehicle and running costs partly from pre-tax salary</li>
              <li>GST savings on vehicle purchase and running costs</li>
              <li>Simplified budgeting with one regular payment</li>
              <li>All vehicle expenses can be bundled into the lease</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NovatedLeaseCalculator;

