          "[Your Name]": "Harrington", // This should be dynamic in a real implementation
          "[Your Phone Number]": "1300 123 456", // This should be dynamic
          "[Your Email]": "harrington@sgfleet.com", // This should be dynamic
        };
        
        // Replace placeholders in subject and content
        for (const [placeholder, value] of Object.entries(replacements)) {
          subject = subject.replace(new RegExp(placeholder, 'g'), value);
          content = content.replace(new RegExp(placeholder, 'g'), value);
        }
        
        // Update the template elements
        const emailSubject = document.getElementById('emailSubject');
        const templateInput = document.getElementById('templateInput');
        
        if (emailSubject) emailSubject.value = subject;
        if (templateInput) templateInput.value = content;
        
        window.NovAssistant.ui.showToast("Template personalized with customer data", "success");
        
        return true;
      } catch (error) {
        AppLogger.error("Error personalizing template", error);
        window.NovAssistant.ui.showToast("Error personalizing template", "error");
        return false;
      }
    },
    
    // Copy template to clipboard
    copyTemplateToClipboard: function() {
      try {
        const emailSubject = document.getElementById('emailSubject');
        const templateInput = document.getElementById('templateInput');
        
        if (!emailSubject || !templateInput) {
          window.NovAssistant.ui.showToast("Template content not found", "error");
          return false;
        }
        
        // Create clipboard content with subject and body
        const clipboardContent = `Subject: ${emailSubject.value}\n\n${templateInput.value}`;
        
        // Copy to clipboard
        navigator.clipboard.writeText(clipboardContent)
          .then(() => {
            window.NovAssistant.ui.showToast("Template copied to clipboard", "success");
          })
          .catch((error) => {
            throw error;
          });
        
        return true;
      } catch (error) {
        AppLogger.error("Error copying template to clipboard", error);
        window.NovAssistant.ui.showToast("Error copying to clipboard", "error");
        return false;
      }
    },
    
    // Open template in Outlook
    openInOutlook: function() {
      try {
        const emailSubject = document.getElementById('emailSubject');
        const templateInput = document.getElementById('templateInput');
        
        if (!emailSubject || !templateInput) {
          window.NovAssistant.ui.showToast("Template content not found", "error");
          return false;
        }
        
        // Get customer email if available
        let recipientEmail = "";
        if (window.NovAssistant.state.currentCustomerIndex !== null) {
          const customers = window.NovAssistant.CustomerManagement.getCustomers();
          const customer = customers[window.NovAssistant.state.currentCustomerIndex];
          if (customer && customer.email) {
            recipientEmail = customer.email;
          }
        }
        
        // Create mailto link
        const subject = encodeURIComponent(emailSubject.value);
        const body = encodeURIComponent(templateInput.value);
        const mailtoLink = `mailto:${recipientEmail}?subject=${subject}&body=${body}`;
        
        // Open link
        window.location.href = mailtoLink;
        
        return true;
      } catch (error) {
        AppLogger.error("Error opening template in Outlook", error);
        window.NovAssistant.ui.showToast("Error opening in Outlook", "error");
        return false;
      }
    },
    
    // Get template by key
    getTemplate: function(templateKey) {
      return this.templates[templateKey] || null;
    }
  };

