  version: "1.0.0",
  initialized: false,

  // Tax rates for different income brackets (2024-2025 rates)
  taxRates: [
    { min: 0, max: 18200, rate: 0, base: 0 },
    { min: 18201, max: 45000, rate: 0.19, base: 0 },
    { min: 45001, max: 120000, rate: 0.325, base: 5092 },
    { min: 120001, max: 180000, rate: 0.37, base: 29467 },
    { min: 180001, max: Infinity, rate: 0.45, base: 51667 },
  ],

  // Default quote settings
  defaultSettings: {
    vehiclePrice: 40000,
    term: 60, // months
    interestRate: 7.9, // percentage
    annualKilometers: 15000,
    fuelCost: 1.8, // per liter
    fuelConsumption: 8.5, // liters per 100km
    insurancePremium: 1200, // annual
    registrationFee: 850, // annual
    servicingCost: 1200, // annual
    tireCost: 1000, // per set
    tireReplacement: 40000, // kilometers
    leaseGuard: true,
    windowTint: true,
    residualValuePercentage: 28.13, // for 5 year lease
    employerFBTType: "fullFBT", // fullFBT, rebatable, exempt
  },

  // Current calculation state
  currentCalculation: null,

  // Initialize the module
  init: async function () {
    try {
      if (this.initialized) {
        return this;
      }

      AppLogger.log("Initializing Quote Calculator module");

      // Set up event listeners
      this.setupCalculatorForm();

      this.initialized = true;
      AppLogger.log("Quote Calculator module initialized successfully");

      return this;
    } catch (error) {
      AppLogger.error("Failed to initialize Quote Calculator module", error);
      throw error;
    }
  },

  // Set up calculator form
  setupCalculatorForm: function () {
    try {
      const calculatorForm = document.querySelector(".calculator-form");
      if (!calculatorForm) return false;

      // Set up vehicle price input
      const vehiclePriceInput = document.getElementById("vehiclePrice");
      if (vehiclePriceInput) {
        vehiclePriceInput.value = this.defaultSettings.vehiclePrice;
        vehiclePriceInput.addEventListener(
          "input",
          this.debounce(this.handleInputChange.bind(this), 500)
        );
      }

      // Set up term selector
      const termSelect = document.getElementById("leaseTerm");
      if (termSelect) {
        termSelect.value = this.defaultSettings.term;
        termSelect.addEventListener(
          "change",
          this.handleInputChange.bind(this)
        );
      }

      // Set up interest rate input
      const interestRateInput = document.getElementById("interestRate");
      if (interestRateInput) {
        interestRateInput.value = this.defaultSettings.interestRate;
        interestRateInput.addEventListener(
          "input",
          this.debounce(this.handleInputChange.bind(this), 500)
        );
      }

      // Set up annual kilometers input
      const annualKmInput = document.getElementById("annualKm");
      if (annualKmInput) {
        annualKmInput.value = this.defaultSettings.annualKilometers;
        annualKmInput.addEventListener(
          "input",
          this.debounce(this.handleInputChange.bind(this), 500)
        );
      }

      // Set up salary input
      const salaryInput = document.getElementById("annualSalary");
      if (salaryInput) {
        // Use customer's salary if available
        if (window.NovAssistant.state.currentCustomerIndex !== null) {
          const customers =
            window.NovAssistant.CustomerManagement.getCustomers();
          const customer =
            customers[window.NovAssistant.state.currentCustomerIndex];
          if (customer && customer.annualSalary) {
            salaryInput.value = customer.annualSalary;
          }
        }

        salaryInput.addEventListener(
          "input",
          this.debounce(this.handleInputChange.bind(this), 500)
        );
