      // Set up employer FBT type selector
      const fbtTypeSelect = document.getElementById("employerFBTType");
      if (fbtTypeSelect) {
        fbtTypeSelect.value = this.defaultSettings.employerFBTType;
        fbtTypeSelect.addEventListener(
          "change",
          this.handleInputChange.bind(this)
        );
      }

      // Set up running costs switches
      const runningCostCheckboxes = document.querySelectorAll(
        ".running-cost-switch"
      );
      runningCostCheckboxes.forEach((checkbox) => {
        checkbox.checked = true;
        checkbox.addEventListener("change", this.handleInputChange.bind(this));
      });

      // Set up aftermarket product checkboxes
      const aftermarketCheckboxes = document.querySelectorAll(
        ".aftermarket-switch"
      );
      aftermarketCheckboxes.forEach((checkbox) => {
        const productId = checkbox.id;
        switch (productId) {
          case "leaseGuard":
            checkbox.checked = this.defaultSettings.leaseGuard;
            break;
          case "windowTint":
            checkbox.checked = this.defaultSettings.windowTint;
            break;
          default:
            checkbox.checked = false;
        }

        checkbox.addEventListener("change", this.handleInputChange.bind(this));
      });

      // Set up calculate button
      const calculateBtn = document.getElementById("calculateQuoteBtn");
      if (calculateBtn) {
        calculateBtn.addEventListener("click", this.calculateQuote.bind(this));
      }

      return true;
    } catch (error) {
      AppLogger.error("Error setting up calculator form", error);
      return false;
    }
  },

  // Debounce function to prevent excessive calculations during input
  debounce: function (func, delay) {
    let timeoutId;
    return function (...args) {
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => func.apply(this, args), delay);
    };
  },

  // Handle input changes
  handleInputChange: function () {
    try {
      // Re-calculate quote
      this.calculateQuote();

      return true;
    } catch (error) {
      AppLogger.error("Error handling input change", error);
      return false;
    }
  },

  // Calculate quote based on form inputs
  calculateQuote: function () {
    try {
      // Get form values
      const formValues = this.getFormValues();
      if (!formValues) return false;

      // Perform calculations
      const calculation = this.performCalculations(formValues);

      // Store calculation result
      this.currentCalculation = calculation;

      // Update UI with results
      this.updateResultsUI(calculation);

      return true;
    } catch (error) {
      AppLogger.error("Error calculating quote", error);
      window.NovAssistant.ui.showToast("Error calculating quote", "error");
      return false;
    }
  },

  // Get values from form inputs
  getFormValues: function () {
    try {
      const values = {};

      // Get vehicle price
      const vehiclePriceInput = document.getElementById("vehiclePrice");
      if (vehiclePriceInput) {
        values.vehiclePrice =
          parseFloat(vehiclePriceInput.value) ||
          this.defaultSettings.vehiclePrice;
      } else {
        values.vehiclePrice = this.defaultSettings.vehiclePrice;
      }

      // Get lease term
      const termSelect = document.getElementById("leaseTerm");
      if (termSelect) {
        values.term = parseInt(termSelect.value) || this.defaultSettings.term;
      } else {
        values.term = this.defaultSettings.term;
      }

      // Get interest rate
      const interestRateInput = document.getElementById("interestRate");
      if (interestRateInput) {
        values.interestRate =
          parseFloat(interestRateInput.value) ||
          this.defaultSettings.interestRate;
      } else {
        values.interestRate = this.defaultSettings.interestRate;
      }

      // Get annual kilometers
      const annualKmInput = document.getElementById("annualKm");
      if (annualKmInput) {
        values.annualKilometers =
          parseInt(annualKmInput.value) ||
          this.defaultSettings.annualKilometers;
      } else {
        values.annualKilometers = this.defaultSettings.annualKilometers;
      }

      // Get annual salary
      const salaryInput = document.getElementById("annualSalary");
      if (salaryInput) {
        values.annualSalary = parseFloat(salaryInput.value) || 80000; // Default to $80,000
      } else {
        values.annualSalary = 80000;
      }

      // Get employer FBT type
      const fbtTypeSelect = document.getElementById("employerFBTType");
      if (fbtTypeSelect) {
        values.employerFBTType =
          fbtTypeSelect.value || this.defaultSettings.employerFBTType;
      } else {
        values.employerFBTType = this.defaultSettings.employerFBTType;
      }

      // Get running costs inclusion
      const fuelCheckbox = document.getElementById("includeFuel");
      values.includeFuel = fuelCheckbox ? fuelCheckbox.checked : true;

      const insuranceCheckbox = document.getElementById("includeInsurance");
      values.includeInsurance = insuranceCheckbox
        ? insuranceCheckbox.checked
        : true;

      const registrationCheckbox = document.getElementById(
        "includeRegistration"
      );
      values.includeRegistration = registrationCheckbox
        ? registrationCheckbox.checked
        : true;

      const servicingCheckbox = document.getElementById("includeServicing");
      values.includeServicing = servicingCheckbox
        ? servicingCheckbox.checked
        : true;

      const tiresCheckbox = document.getElementById("includeTires");
      values.includeTires = tiresCheckbox ? tiresCheckbox.checked : true;

      // Get aftermarket product inclusion
      const leaseGuardCheckbox = document.getElementById("leaseGuard");
      values.leaseGuard = leaseGuardCheckbox
        ? leaseGuardCheckbox.checked
        : this.defaultSettings.leaseGuard;

      const windowTintCheckbox = document.getElementById("windowTint");
      values.windowTint = windowTintCheckbox
        ? windowTintCheckbox.checked
        : this.defaultSettings.windowTint;

      const exteriorProtectionCheckbox =
        document.getElementById("exteriorProtection");
      values.exteriorProtection = exteriorProtectionCheckbox
        ? exteriorProtectionCheckbox.checked
        : false;

      const interiorProtectionCheckbox =
        document.getElementById("interiorProtection");
      values.interiorProtection = interiorProtectionCheckbox
        ? interiorProtectionCheckbox.checked
        : false;

      const dashCamCheckbox = document.getElementById("dashCam");
      values.dashCam = dashCamCheckbox ? dashCamCheckbox.checked : false;

      // Calculate residual value percentage based on term
      switch (values.term) {
        case 12: // 1 year
          values.residualValuePercentage = 65.63;
          break;
        case 24: // 2 years
          values.residualValuePercentage = 56.25;
          break;
        case 36: // 3 years
          values.residualValuePercentage = 46.88;
          break;
        case 48: // 4 years
          values.residualValuePercentage = 37.5;
          break;
        case 60: // 5 years
          values.residualValuePercentage = 28.13;
          break;
        default:
          values.residualValuePercentage = 28.13;
      }

      // Running costs values
      values.fuelCost = this.defaultSettings.fuelCost;
      values.fuelConsumption = this.defaultSettings.fuelConsumption;
      values.insurancePremium = this.defaultSettings.insurancePremium;
      values.registrationFee = this.defaultSettings.registrationFee;
      values.servicingCost = this.defaultSettings.servicingCost;
      values.tireCost = this.defaultSettings.tireCost;
      values.tireReplacement = this.defaultSettings.tireReplacement;

      return values;
    } catch (error) {
      AppLogger.error("Error getting form values", error);
      return null;
    }
  },

  // Perform lease calculations
  performCalculations: function (formValues) {
    try {
      const result = {
        inputs: formValues,
        finance: {},
        runningCosts: {},
        aftermarket: {},
        tax: {},
        comparison: {},
      };

      // Calculate vehicle costs
      // Remove GST from vehicle price for novated lease
      const vehiclePriceExGST = formValues.vehiclePrice / 1.1;
      result.finance.vehiclePriceExGST = vehiclePriceExGST;
      result.finance.gstSavings = formValues.vehiclePrice - vehiclePriceExGST;

      // Calculate residual value
      result.finance.residualValue =
        (vehiclePriceExGST * formValues.residualValuePercentage) / 100;

      // Calculate finance amount
      result.finance.financeAmount =
        vehiclePriceExGST - result.finance.residualValue;

      // Calculate monthly repayment
      const monthlyInterestRate = formValues.interestRate / 100 / 12;
      result.finance.monthlyRepayment = this.calculatePMT(
        monthlyInterestRate,
        formValues.term,
        -vehiclePriceExGST,
        result.finance.residualValue
      );

      // Calculate running costs
      const totalKilometers =
        formValues.annualKilometers * (formValues.term / 12);

      // Fuel costs
      if (formValues.includeFuel) {
        const fuelConsumptionLiters =
          (formValues.fuelConsumption / 100) * totalKilometers;
        const totalFuelCost = fuelConsumptionLiters * formValues.fuelCost;
        result.runningCosts.fuel = totalFuelCost / formValues.term;
      } else {
        result.runningCosts.fuel = 0;
      }

      // Insurance costs
      if (formValues.includeInsurance) {
        result.runningCosts.insurance = formValues.insurancePremium / 12;
      } else {
        result.runningCosts.insurance = 0;
      }

      // Registration costs
      if (formValues.includeRegistration) {
        result.runningCosts.registration = formValues.registrationFee / 12;
      } else {
        result.runningCosts.registration = 0;
      }

      // Servicing costs
      if (formValues.includeServicing) {
        result.runningCosts.servicing = formValues.servicingCost / 12;
      } else {
        result.runningCosts.servicing = 0;
      }

      // Tire costs
      if (formValues.includeTires) {
        const totalTireSets = Math.ceil(
          totalKilometers / formValues.tireReplacement
        );
        const totalTireCost = totalTireSets * formValues.tireCost;
        result.runningCosts.tires = totalTireCost / formValues.term;
      } else {
        result.runningCosts.tires = 0;
      }

      // Calculate total running costs
      result.runningCosts.total =
        result.runningCosts.fuel +
        result.runningCosts.insurance +
        result.runningCosts.registration +
        result.runningCosts.servicing +
        result.runningCosts.tires;

      // Calculate aftermarket costs
      // LeaseGuard
      if (formValues.leaseGuard) {
        result.aftermarket.leaseGuard = 18; // $18 per month
      } else {
        result.aftermarket.leaseGuard = 0;
      }

      // Window tinting
      if (formValues.windowTint) {
        result.aftermarket.windowTint = 600 / formValues.term; // $600 spread over lease term
      } else {
        result.aftermarket.windowTint = 0;
      }

      // Exterior protection
      if (formValues.exteriorProtection) {
        result.aftermarket.exteriorProtection = 1200 / formValues.term; // $1,200 spread over lease term
      } else {
        result.aftermarket.exteriorProtection = 0;
      }

      // Interior protection
      if (formValues.interiorProtection) {
        result.aftermarket.interiorProtection = 900 / formValues.term; // $900 spread over lease term
      } else {
        result.aftermarket.interiorProtection = 0;
      }

      // Dash cam
      if (formValues.dashCam) {
        result.aftermarket.dashCam = 600 / formValues.term; // $600 spread over lease term
      } else {
        result.aftermarket.dashCam = 0;
      }

      // Calculate total aftermarket costs
      result.aftermarket.total =
        result.aftermarket.leaseGuard +
        result.aftermarket.windowTint +
        result.aftermarket.exteriorProtection +
        result.aftermarket.interiorProtection +
        result.aftermarket.dashCam;

      // Calculate pre-tax and post-tax splits based on employer FBT type
      if (formValues.employerFBTType === "exempt") {
        // Exempt employers - all costs can be pre-tax
        result.tax.preTaxAmount =
          result.finance.monthlyRepayment +
          result.runningCosts.total +
          result.aftermarket.total;
        result.tax.postTaxAmount = 0;
      } else if (formValues.employerFBTType === "rebatable") {
        // Rebatable employers - standard ECM with slightly more favorable split
        const totalCost =
          result.finance.monthlyRepayment +
          result.runningCosts.total +
          result.aftermarket.total;
        result.tax.preTaxAmount = totalCost * 0.85; // 85% pre-tax
        result.tax.postTaxAmount = totalCost * 0.15; // 15% post-tax
      } else {
        // Standard employers - Employee Contribution Method (ECM)
        // Finance comes from post-tax, running costs from pre-tax
        result.tax.preTaxAmount =
          result.runningCosts.total + result.aftermarket.total;
        result.tax.postTaxAmount = result.finance.monthlyRepayment;
      }

      // Calculate total monthly cost
      result.totalMonthlyCost =
        result.tax.preTaxAmount + result.tax.postTaxAmount;

      // Calculate tax implications
      result.tax.taxRate = this.getTaxRate(formValues.annualSalary);
      result.tax.taxSavings = result.tax.preTaxAmount * result.tax.taxRate;

      // Calculate net monthly cost after tax savings
      result.netMonthlyCost = result.totalMonthlyCost - result.tax.taxSavings;

      // Calculate comparison with other financing options

      // Car loan comparison
      const loanInterestRate = (formValues.interestRate + 1) / 100 / 12; // Slightly higher rate for loan
      const loanMonthlyPayment = this.calculatePMT(
        loanInterestRate,
        formValues.term,
        -formValues.vehiclePrice,
        0
      );

      result.comparison.carLoan = {
        monthlyPayment: loanMonthlyPayment,
        totalMonthlyCost: loanMonthlyPayment + result.runningCosts.total,
        savings:
          result.comparison.carLoan.totalMonthlyCost - result.netMonthlyCost,
      };

      // Cash purchase comparison
      const opportunityCost = (formValues.vehiclePrice * 0.05) / 12; // 5% annual return if invested

      result.comparison.cashPurchase = {
        vehicleCost: formValues.vehiclePrice / formValues.term,
        opportunityCost: opportunityCost,
        totalMonthlyCost:
          formValues.vehiclePrice / formValues.term +
          result.runningCosts.total +
          opportunityCost,
        savings:
          result.comparison.cashPurchase.totalMonthlyCost -
          result.netMonthlyCost,
      };

      return result;
    } catch (error) {
      AppLogger.error("Error performing calculations", error);
      return null;
    }
  },

  // Calculate PMT (loan payment) function
  calculatePMT: function (rate, nper, pv, fv = 0, type = 0) {
    if (rate === 0) return -(pv + fv) / nper;

    const pvif = Math.pow(1 + rate, nper);
    let pmt = (rate / (pvif - 1)) * -(pv * pvif + fv);

    if (type === 1) {
      pmt = pmt / (1 + rate);
    }

    return pmt;
  },

  // Get tax rate based on income
  getTaxRate: function (annualSalary) {
    for (const bracket of this.taxRates) {
      if (annualSalary >= bracket.min && annualSalary <= bracket.max) {
        return bracket.rate;
      }
    }
    return 0;
  },

  // Update results UI
  updateResultsUI: function (calculation) {
    try {
      if (!calculation) return false;

      // Update main result
      const mainResultElement = document.querySelector(".main-result");
      if (mainResultElement) {
        mainResultElement.textContent = `${calculation.netMonthlyCost.toFixed(
          2
        )}/month`;
      }

      // Update detailed results
      const detailedResultsElement = document.querySelector(
        ".calculator-results"
      );
      if (detailedResultsElement) {
        detailedResultsElement.innerHTML = `
            <p><strong>Total Monthly Cost:</strong> ${calculation.totalMonthlyCost.toFixed(
              2
            )}</p>
            <p><strong>Pre-Tax Amount:</strong> ${calculation.tax.preTaxAmount.toFixed(
              2
            )}</p>
            <p><strong>Post-Tax Amount:</strong> ${calculation.tax.postTaxAmount.toFixed(
              2
            )}</p>
            <p><strong>Monthly Tax Benefit:</strong> ${calculation.tax.taxSavings.toFixed(
              2
            )}</p>
            <p><strong>Finance Amount:</strong> ${calculation.finance.monthlyRepayment.toFixed(
              2
            )}/month</p>
            <p><strong>Running Costs:</strong> ${calculation.runningCosts.total.toFixed(
              2
            )}/month</p>
            <p><strong>Aftermarket Products:</strong> ${calculation.aftermarket.total.toFixed(
              2
            )}/month</p>
            <p><strong>Residual Value:</strong> ${calculation.finance.residualValue.toFixed(
              2
            )}</p>
          `;
      }

      // Update comparison results
      const comparisonResultsElement = document.querySelector(
        ".comparison-results"
      );
      if (comparisonResultsElement) {
        comparisonResultsElement.innerHTML = `
            <h4>Comparison with Other Options</h4>
            <p><strong>Car Loan:</strong> ${calculation.comparison.carLoan.totalMonthlyCost.toFixed(
              2
            )}/month</p>
            <p><strong>Cash Purchase:</strong> ${calculation.comparison.cashPurchase.totalMonthlyCost.toFixed(
              2
            )}/month</p>
            <p><strong>Monthly Savings vs Loan:</strong> ${calculation.comparison.carLoan.savings.toFixed(
              2
            )}</p>
            <p><strong>Monthly Savings vs Cash:</strong> ${calculation.comparison.cashPurchase.savings.toFixed(
              2
            )}</p>
          `;
      }

      return true;
    } catch (error) {
      AppLogger.error("Error updating results UI", error);
      return false;
    }
  },

  // Generate a detailed quote PDF
  generateQuotePDF: function () {
    try {
      if (!this.currentCalculation) {
        window.NovAssistant.ui.showToast("No calculation available", "warning");
        return false;
      }

      // In a real implementation, this would generate a PDF
      // For now, we'll just show a success message
      window.NovAssistant.ui.showToast(
        "Quote PDF generation not implemented in demo",
        "info"
      );

      return true;
    } catch (error) {
      AppLogger.error("Error generating quote PDF", error);
      return false;
    }
  },

  // Save quote to customer record
  saveQuoteToCustomer: function () {
    try {
      if (!this.currentCalculation) {
        window.NovAssistant.ui.showToast("No calculation available", "warning");
        return false;
      }

      // Check if we have a current customer
      if (window.NovAssistant.state.currentCustomerIndex === null) {
        window.NovAssistant.ui.showToast("No customer selected", "warning");
        return false;
      }

      const customers = window.NovAssistant.CustomerManagement.getCustomers();
      const customer =
        customers[window.NovAssistant.state.currentCustomerIndex];

      if (!customer) {
        window.NovAssistant.ui.showToast("Customer not found", "error");
        return false;
      }

      // Initialize quotes array if needed
      if (!customer.quotes) {
        customer.quotes = [];
      }

      // Add quote to customer record
      customer.quotes.push({
        id: Date.now(),
        timestamp: new Date().toISOString(),
        vehiclePrice: this.currentCalculation.inputs.vehiclePrice,
        term: this.currentCalculation.inputs.term,
        totalMonthlyCost: this.currentCalculation.totalMonthlyCost,
        netMonthlyCost: this.currentCalculation.netMonthlyCost,
        taxSavings: this.currentCalculation.tax.taxSavings,
        calculation: this.currentCalculation,
      });

      // Save customer data
      window.NovAssistant.CustomerManagement.saveCustomers();

      // Show success message
      window.NovAssistant.ui.showToast(
        "Quote saved to customer record",
        "success"
      );

      return true;
    } catch (error) {
      AppLogger.error("Error saving quote to customer", error);
      return false;
    }
  },

  // Generate a comparison chart between novated lease and other options
  generateComparisonChart: function () {
    try {
      if (!this.currentCalculation) {
        window.NovAssistant.ui.showToast("No calculation available", "warning");
        return false;
      }

      // Build chart data
      const chartData = [
        {
          label: "Novated Lease",
          value: this.currentCalculation.netMonthlyCost,
          color: "#004225",
        },
        {
          label: "Car Loan",
          value: this.currentCalculation.comparison.carLoan.totalMonthlyCost,
          color: "#ff6b35",
        },
        {
          label: "Cash Purchase",
          value:
            this.currentCalculation.comparison.cashPurchase.totalMonthlyCost,
          color: "#2c3e50",
        },
      ];

      // Sort data by value (lowest to highest)
      chartData.sort((a, b) => a.value - b.value);

      // Create chart canvas
      const chartCanvas = document.getElementById("comparisonChart");
      if (!chartCanvas) {
        window.NovAssistant.ui.showToast("Chart canvas not found", "error");
        return false;
      }

      // Clear existing chart
      chartCanvas.innerHTML = "";

      // Create chart elements
      chartData.forEach((item) => {
        const barContainer = document.createElement("div");
        barContainer.className = "chart-bar-container";

        const label = document.createElement("div");
        label.className = "chart-label";
        label.textContent = item.label;

        const bar = document.createElement("div");
        bar.className = "chart-bar";
        bar.style.width = `${
          (item.value / chartData[chartData.length - 1].value) * 100
        }%`;
        bar.style.backgroundColor = item.color;

        const value = document.createElement("div");
        value.className = "chart-value";
        value.textContent = `${item.value.toFixed(2)}`;

        barContainer.appendChild(label);
        barContainer.appendChild(bar);
        barContainer.appendChild(value);

        chartCanvas.appendChild(barContainer);
      });

      // Add savings information
      const savingsInfo = document.createElement("div");
      savingsInfo.className = "chart-savings-info";

      const bestOption = chartData[0].label;
      if (bestOption === "Novated Lease") {
        savingsInfo.innerHTML = `
            <p>The novated lease option saves you:</p>
            <ul>
              <li><strong>${(
                this.currentCalculation.comparison.carLoan.totalMonthlyCost -
                this.currentCalculation.netMonthlyCost
              ).toFixed(2)}/month</strong> compared to a car loan</li>
              <li><strong>${(
                this.currentCalculation.comparison.cashPurchase
                  .totalMonthlyCost - this.currentCalculation.netMonthlyCost
              ).toFixed(2)}/month</strong> compared to a cash purchase</li>
            </ul>
            <p>That's a savings of <strong>${(
              (this.currentCalculation.comparison.carLoan.totalMonthlyCost -
                this.currentCalculation.netMonthlyCost) *
              this.currentCalculation.inputs.term
            ).toFixed(2)}</strong> over the ${
          this.currentCalculation.inputs.term / 12
        }-year term!</p>
          `;
      } else {
        savingsInfo.innerHTML = `
            <p>In this scenario, a ${bestOption.toLowerCase()} appears to be more cost-effective.</p>
            <p>However, novated leasing offers these additional benefits:</p>
            <ul>
              <li>Simplified budgeting with one regular payment</li>
              <li>No GST on vehicle purchase or running costs</li>
              <li>Potential for better leasing rates than retail financing</li>
              <li>Option to include running costs in your package</li>
            </ul>
          `;
      }

      chartCanvas.appendChild(savingsInfo);

      return true;
    } catch (error) {
      AppLogger.error("Error generating comparison chart", error);
      return false;
    }
  },
};

